import { useState } from "react";
import QuestionTableRow from "./QuestionTableRow";

const EditExamAccordin = ({ object, name, onDelete, id, onEdit, topics }) => {
  const [toggle, setToggle] = useState(true);
  const onClick = () => {
    setToggle(!toggle);
  };

  const questionList = () => {
    console.log(topics);
    const questions = topics.filter((obj) => object[id] === obj.topicId);
    console.log(questions);
    return questions;
  };

  return (
    <div className=" bg-white border">
      <div className="accordion accordion-flush">
        <div className="accordion-item">
          <h2 className="accordion-header position-relative ">
            <button
              onClick={onClick}
              className={`accordion-button ${toggle && "collapsed"}`}
              type="button"
            >
              <h5>{object[name]}</h5>
              <p></p>
            </button>
            <div className="  row z-3 position-absolute end-0 me-5  top-0 mt-3 ">
              <button
                onClick={() => onEdit(object[id])}
                className="btn btn-outline-success  btn-sm me-4 col"
              >
                Edit
              </button>
              <button
                onClick={() => onDelete(object[id])}
                className="btn btn-outline-danger btn-sm me-3 col "
              >
                Delete
              </button>
            </div>
          </h2>
          <div className={`accordion-collapse ${toggle && "collapse"}`}>
            <div className="accordion-body text-primary p-0 border border-primary">
              <h5 className="text-primary border  text-center">Questions</h5>
                <table className="table">
                  <thead>
                    <tr>
                      <th scope="col-2 ">Question Id</th>
                      <th scope="col">Question</th>
                    </tr>
                  </thead>
                  <tbody>
                    <QuestionTableRow array={questionList()}/>
                  </tbody>
                </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditExamAccordin;
