import { createContext, useContext, useState } from "react";

const TopicContext = createContext(null);

export const TopicProvider =({children})=>{
    const [topics, setTopics] = useState([
        {
          topicId: "1",
          topicName: "Oops",
        },
        {
          topicId: "2",
          topicName: "Methods",
        },
      ]);
      const fetchTopic = async () => {
        const topicList = await getTopics();
        setTopics([...topics, ...topicList]);
        return "success";
      };
    
      const getTopics = async () => {
        const res = await fetch(
          "https://localhost:8443/onlineexam/control/findAllTopics"
        );
        const data = await res.json();
      
        const { topicList } = data;
        return topicList;
      };

    return <TopicContext.Provider value={{topics,setTopics,fetchTopic}}>
        {children}
    </TopicContext.Provider>

}

export const useTopicContext =()=>{
    return useContext(TopicContext);
}
