  //   );
  //   const data = await res.json();
  
  //   const { topicList } = data;
  //   return topicList;
  // };

  return (
    <>
      <MainContent text={"Topic"} to="add-topic" back="/admin/topic" />
      <AccordinMaker objects={topics} id={"topicId"} name={"topicName"} onDelete={onDelete} />
    </>
  );
};

export default Topics;
