import React, { useEffect, useState } from 'react'
import MainContent from '../common/MainContent';
import AccordinMaker from '../common/AccordinMaker';


function User() {

  const [users,setUsers] = useState([]);

    const user = [{
        id:1,
        name:"Kamal"
    },{
        id:2,
        name:"Ganesh"
    }]

    // useEffect(()=>{
    //   fetchUsers();
    //   return()=>{
    //     setUsers([]);
    //   }
    // },[])

    useEffect(()=>{   
      fetchUsers();
      return ()=>{
        setUsers([]);
      }
  
    },[]);

    const fetchUsers =async()=>{

      const res = await fetch("https://localhost:8443/onlineexam/control/findAllUsers");

      const data = await res.json();

     const{userList} = data;

      setUsers(userList);
    }

    const onEdit =(id)=>{
      navigate(`edit/userId/${id}`);
    
     }

    const text ={
        header:"Users",
        btnText:"User"
    }


  return (
    <>
    <MainContent text={text} to="add-user" back="/admin/user"/>
    <AccordinMaker objects={users} id={"partyId"} name={"firstName"} onDelete={""} onEdit={onEdit} /> 
    </>
  )
}

export default User
