import React, { useState } from "react";
import Input from "./Input";

const Login = ({ onLogin }) => {
  const [user, setUser] = useState("");
  const [pass, setPass] = useState("");
  const onSubmit = (e) => {
    if (!user) {
      alert("please enter user name");
      return;
    }
    if (!pass) {
      alert("please enter password");
      return;
    }
    e.preventDefault();
    let USERNAME = user;
    let PASSWORD = pass;

    onLogin({ USERNAME, PASSWORD });
    setUser("");
    setPass("");
  };

  return (
    <div className="container">
      <div className="row mt-5">
        <div className="col-4 mt-5"></div>
        <div className="col-4  mt-5 p-3 border border-2">
          
           <div className="text-center fw-bold   p-3 border-bottom ">Login</div>
         
          <form onSubmit={onSubmit}>
            <Input
              name={"user"}
              text="User"
              state={user}
              setStateFun={setUser}
              type={"text"}
              placeholder={""}
            />
            <Input
              name={"InputPassword"}
              text="Password"
              state={pass}
              setStateFun={setPass}
              type={"password"}
              placeholder={""}
              passView={true}
            />
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </form>
        </div>
        <div className="col-4"></div>
      </div>
    </div>
  );
};

export default Login;
