import React from 'react'
import OptionMaker from './OptionMaker'

const DropDown = ({id,text,state,setState,array}) => {

  return (
    <div className="mb-3">
          <label htmlFor={id} className="form-label">{text}</label>
          <select className="form-control"  id={id} value={state} onChange={e=>setState(e.target.value)}>
           <OptionMaker array={array} propertyName={id}/>
          </select>
        </div>
  )
}

export default DropDown
