import React, { useEffect, useState } from 'react'

const PaletteButton = ({onSequenceNumClick ,question}) => {
    const [viewed,setViewed] =useState(false);
    var viewedOnce ="btn-warning";
    
    useEffect(()=>{
        checkAnswered();
        console.log("clicked")
    },[question]);
    
    const checkAnswered =()=>{
       if(question?.answer!==""){
        viewedOnce="btn-success"
       }
    }

    const HandleClick =(sequenceNum)=>{
        setViewed(true);
        onSequenceNumClick(sequenceNum);
    
    }
  return (
    <div class="btn-group m-2" role="group">
    <button
      type="button"
      class={`btn  px-3 ${viewed? viewedOnce :"btn-outline-secondary"}`}
      onClick={()=>HandleClick(question.sequenceNum)}
    >
      {question.sequenceNum}
    </button>
  </div>
  )
}

export default PaletteButton
