import React, { useState } from "react";
import Input from "./Input";
import { useAuth } from "../auth";

const Login = ({ onLogin }) => {
  const auth=useAuth();
  const [user, setUser] = useState("");
  const [pass, setPass] = useState("");
  const onSubmit = (e) => {
    
    auth.setUser(user)
    // if (!user) {
    //   alert("please enter user name");
    //   return;
    // }
    // if (!pass) {
    //   alert("please enter password");
    //   return;
    // }
    // e.preventDefault();
    // let USERNAME = user;
    // let PASSWORD = pass;

    // onLogin({ USERNAME, PASSWORD });
    setUser("");
    setPass("");
  };

  return (
    <div className="container">
      <div className="row mt-5 align-items-center justify-content-center">
        
        <div className="col-5 mt-5  border border-2">
          
           <div className="text-center fw-bold   p-3 border-bottom ">Login</div>
         
          <form className="p-2" onSubmit={onSubmit}>
            <Input
              name={"user"}
              text="User"
              state={user}
              setStateFun={setUser}
              type={"text"}
              placeholder={""}
            />
            <Input
              name={"InputPassword"}
              text="Password"
              state={pass}
              setStateFun={setPass}
              type={"password"}
              placeholder={""}
              passView={true}
            />
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </form>
        </div>
        
      </div>
    </div>
  );
};

export default Login;
