import React from 'react'
import { useAuth } from '../auth'
import { Navigate } from 'react-router-dom';

const RequiredAuth = ({children}) => {
    const auth = useAuth();
    if(!auth.user){
        return <Navigate to='/login'></Navigate>
    }
  return children
}

export default RequiredAuth
