import React, { useState } from 'react'
import Input from './Input';

const UserRegister = ({onRegister}) => {

    const [firstName,setFirstName]=useState("");
    const [lastName,setLastName]=useState("");
    const [userLoginId,setUserLoginId]=useState("");
    const [currentPassword,setCurrentPassword]=useState("");
    const [currentPasswordVerify,setCurrentPasswordVerify]=useState("");

    const onSubmit=(e)=>{
      e.preventDefault();
      if(!firstName){
        alert("First Name is required");
        return;
      }
      if(!lastName){
        alert("Last Name is required");
        return;
      }
      if(!userLoginId){
        alert("User Login Id is required");
        return;
      }
      if(!currentPassword){
        alert("Current Password is required");
        return;
      }
      if(!currentPasswordVerify){
        alert("Current Password Verify is required");
        return;
      }
      onRegister({firstName,lastName,userLoginId,currentPassword,currentPasswordVerify})
      setFirstName('');        setLastName('');
      setUserLoginId('');
      setCurrentPassword('');
      setCurrentPasswordVerify('')
    }
  return (
    <div className="container">
      <form onSubmit={onSubmit}>
      <Input
          name={"userLoginId"}
          text="User Login Id"
          state={userLoginId}
          setStateFun={setUserLoginId}
          type={"text"}
          placeholder={""}
        />
      <Input
          name={"firstName"}
          text="First Name"
          state={firstName}
          setStateFun={setFirstName}
          type={"text"}
          placeholder={""}
        />
       <Input
          name={"lastName"}
          text="Last Name"
          state={lastName}
          setStateFun={setLastName}
          type={"text"}
          placeholder={""}
        />
        <Input
          name={"currentPassword"}
          text="Current Password"
          state={currentPassword}
          setStateFun={setCurrentPassword}
          type={"password"}
          passView={true}
          placeholder={""}
        />
        <Input
          name={"currentPasswordVerify"}
          text="Current Password Verify"
          state={currentPasswordVerify}
          setStateFun={setCurrentPasswordVerify}
          type={"password"}
          placeholder={""}
          passView={true}
        />
       
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  )
}

export default UserRegister
