import React, { useEffect, useState } from "react";
import MainContent from "../common/MainContent";
import AccordinMaker from "../common/AccordinMaker";
import { useExamContext } from "./examData";
import { useNavigate } from "react-router-dom";

const Exam = () => {
  const navigate = useNavigate();
  const { exams, setExams, onDelete } = useExamContext();

  useEffect(() => {
    fetchExam();
    return () => {
      setExams([]);
    };
  }, []);

  const fetchExam = async () => {
    const result = await getExams();
    const examList = result.examList;
    setExams([...exams, ...examList]);
  };

  const getExams = async () => {
    const res = await fetch(
      "https://localhost:8443/onlineexam/control/findAllExams"
    );
    const data = await res.json();
    return data;
  };

  const onEdit = (id) => {
    navigate(`edit/examId/${id}`);
  };
  const text = {
    header: "Exam",
    btnText: "Exam",
  };

  return (
    <>
   
<button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#staticBackdrop">
  Launch static backdrop modal
</button>


<div class="modal fade show" id="staticBackdrop" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="staticBackdropLabel">Modal title</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Understood</button>
      </div>
    </div>
  </div>
</div>
    
      <div class="alert alert-success col-5" role="alert">
        <div className="row ">
          <div className="col-10">
            <h4 class="alert-heading">Well done!</h4>
          </div>
          <div className="col-2 d-grid gap-2 d-md-flex justify-content-md-end">
         
            <button
              type="button"
              class="btn-close "
              aria-label="Close"
            ></button>
          </div>
        </div>
        <p>
          Aww yeah, you successfully read this important alert message. This
          example text is going to run a bit longer so that you can see how
          spacing within an alert works with this kind of content.
        </p>
        <hr />
        <p class="mb-0">
          Whenever you need to, be sure to use margin utilities to keep things
          nice and tidy.
        </p>
      </div>
      <MainContent text={text} to="add-exam" back="/admin/exam" />
      <AccordinMaker
        objects={exams}
        id={"examId"}
        name={"examName"}
        onDelete={onDelete}
        onEdit={onEdit}
      />
    </>
  );
};

export default Exam;
