import React, { useEffect, useState } from 'react'

const PaletteButton = ({onSequenceNumClick ,question}) => {
    const [viewed,setViewed] =useState(false);
    const [color,setColor] =useState("btn-warning");
    
    useEffect(()=>{
        checkAnswered();
        console.log(question)
        console.log("clicked")
    },[question]);
    
    const checkAnswered =()=>{
        setViewed(false);
        if(question?.answer){
            setColor("btn-success")
            setViewed(true);
        }else{
            setColor("btn-warning")
            setViewed(true);
        }

       
    }

    const HandleClick =(sequenceNum)=>{
        setViewed(true);
        onSequenceNumClick(sequenceNum);
    
    }
  return (
    <div class="btn-group m-2" role="group">
    <button
      type="button"
      class={`btn  px-3 ${viewed? color :"btn-outline-secondary"}`}
      onClick={()=>HandleClick(question.sequenceNum)}
    >
      {question.sequenceNum}
    </button>
  </div>
  )
}

export default PaletteButton
