import React, { useState } from "react";
import Input from "./Input";

const Login = ({ onLogin }) => {
  const [user, setUser] = useState("");
  const [pass, setPass] = useState("");
  const onSubmit = (e) => {
    if (!user) {
      alert("please enter user name");
      return;
    }
    if (!pass) {
      alert("please enter password");
      return;
    }
    e.preventDefault();
    let USERNAME = user;
    let PASSWORD = pass;

    onLogin({ USERNAME, PASSWORD });
    setUser("");
    setPass("");
  };

  return (
    <div className="row ms-auto mt-5 border">
      
        <div className="col-3  mt-60 border ">
          <form onSubmit={onSubmit}>
            <Input
              name={"user"}
              text="User"
              state={user}
              setStateFun={setUser}
              type={"text"}
              placeholder={""}
            />
            <Input
              name={"InputPassword"}
              text="Password"
              state={pass}
              setStateFun={setPass}
              type={"password"}
              placeholder={""}
              passView={true}
            />
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </form>
        
      </div>
    </div>
  );
};

export default Login;
