import React, { useEffect, useState } from "react";
import MainContent from "./MainContent";
import AccordinMaker from "./AccordinMaker";
import { useTopicContext } from "./topicData";


const Topics = () => {
  const {topics,setTopics} = useTopicContext();

  useEffect(()=>{
    fetchTopic();
    return()=>{
      setTopics([])
    }
  },[]);

  const fetchTopic = async()=>{
    const  topicList = await getTopics();
    setTopics([...topics,...topicList])
  }

  const getTopics = async()=>{
    const res = await fetch("https://localhost:8443/onlineexam/control/findAllTopics");
    const text = await res.text();
    const data = JSON.parse(text.substring(2));
    const {topicList} =data;
    return topicList;
  }
 
  return (
    <>
      <MainContent text={"Topic"} to="add-topic" back="/admin/topic" />
      <AccordinMaker objects={topics} id={"topicId"}  name={"topicName"} />
    </>
  );
};

export default Topics;
