import React, { createContext, useEffect, useState } from "react";
import MainContent from "../common/MainContent";
import { useParams } from "react-router-dom";
import ExamTopicTable from "./ExamTopicTable";
import AccordinMaker from "../common/AccordinMaker";
export const EditExamContext = createContext();
const EditExam = () => {
  const [examList, setExamList] = useState([]);
  const [examTopicMap, setExamTopicMap] = useState([]);
  const { examId } = useParams();
  const text = {
    header: "Exam-Topic-Mapping",
    btnText: "Topic to Exam ",
  };

  useEffect(() => {
    fetchExamList();
    return () => {
      setExamList([]);
    };
  }, []);

  const fetchExamList = async () => {
    const res = await fetch(
      `https://localhost:8443/onlineexam/control/getExamDetails?examId=${examId}`
    );
    const { examList } = await res.json();
    console.log(examList);
    setExamList(examList);
  };

  const onCreateExamTopicMappingMaster = async (examTopicMappingDetails) => {
    console.log(examTopicMappingDetails);
    const res = await fetch(
      "https://localhost:8443/onlineexam/control/createExamTopicMappingMasterRecord",
      {
        method: "POST",
        headers: {
          "Content-type": "application/json",
        },
        body: JSON.stringify(examTopicMappingDetails),
      }
    );
    const data = await res.json();

    const { examTopicMappingMasterRecord } = data;

    setExamTopicMap([...examTopicMap, examTopicMappingMasterRecord]);
  };

  return (
    <EditExamContext.Provider
      value={{ examId, onCreateExamTopicMappingMaster }}
    >
      <MainContent
        text={text}
        to="Topic to Exam"
        back={`/admin/exam/edit/examId/${examId}`}
      />
      <ExamTopicTable
        examId={examId}
        examTopicMap={examTopicMap}
        setExamTopicMap={setExamTopicMap}
      />
      <div className="mt-2">
        <div className=" bg-white border">
          <div className="accordion accordion-flush">
            <div className="accordion-item">
              <h2 className="accordion-header position-relative ">
                <button
                  onClick={onClick}
                  className={`accordion-button ${toggle && "collapsed"}`}
                  type="button"
                >
                  <h5>{object[name]}</h5>
                  <p></p>
                </button>
                <div className="  row z-3 position-absolute end-0 me-5  top-0 mt-3 ">
                  <button
                    onClick={() => onEdit(object[id])}
                    className="btn btn-outline-success  btn-sm me-4 col"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => onDelete(object[id])}
                    className="btn btn-outline-danger btn-sm me-3 col "
                  >
                    Delete
                  </button>
                </div>
              </h2>
              <div className={`accordion-collapse ${toggle && "collapse"}`}>
                <div className="accordion-body">
                  {content && JSON.stringify(content)}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </EditExamContext.Provider>
  );
};

export default EditExam;
